<?php
// Create directory if it doesn't exist
if (!file_exists('images')) {
    mkdir('images', 0777, true);
}

// Create placeholder images
$images = [
    'michelin-logo.png' => 'data:image/png;base64,',
    'bridgestone-logo.png' => 'data:image/png;base64,',
    'goodyear-logo.png' => 'data:image/png;base64,',
    'continental-logo.png' => 'data:image/png;base64,',
    'hero-bg.jpg' => 'data:image/jpeg;base64,'
];

foreach ($images as $filename => $base64) {
    $data = base64_decode(preg_replace('#^data:image/\w+;base64,#i', '', $base64));
    file_put_contents("images/$filename", $data);
}

echo "Images created successfully!\n";
?> 